import { useEffect, useState } from 'react';
import NavBar from '../components/NavBar';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Navigation } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/navigation';

function Home() {
  const [items, setItems] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [hoveredCardId, setHoveredCardId] = useState(null);

  useEffect(() => {
    fetch('/get_items')
      .then(res => res.json())
      .then(data => setItems(data.items))
      .catch(err => console.error('Error fetching items:', err));
  }, []);

  return (
    <>
      <NavBar />
      <main style={{ padding: '150px 20px 50px' }}>
        <div style={gridStyle}>
          {items.map(item => (
            <div
              key={item.id}
              style={{
                ...cardStyle,
                transform: hoveredCardId === item.id ? 'scale(1.05)' : 'scale(1)',
                boxShadow: hoveredCardId === item.id
                  ? '0 6px 12px rgba(0,0,0,0.15)'
                  : '0 4px 8px rgba(0,0,0,0.1)'
              }}
              onClick={() => setSelectedItem(item)}
              onMouseEnter={() => setHoveredCardId(item.id)}
              onMouseLeave={() => setHoveredCardId(null)}
            >
              <img
                src={
                  hoveredCardId === item.id && item.image_paths?.[1]
                    ? item.image_paths[1]
                    : item.image_path
                }
                alt={item.name}
                style={imageStyle}
              />
              <h3>{item.name}</h3>
              <p>{item.description}</p>
              {item.rent_price && <p>Rent: ${item.rent_price}</p>}
              {item.buy_price && <p>Buy: ${item.buy_price}</p>}
            </div>
          ))}
        </div>
      </main>

      {selectedItem && (
        <div className="modal-backdrop" onClick={() => setSelectedItem(null)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <Swiper
              spaceBetween={10}
              slidesPerView={1}
              navigation
              loop={true}
              modules={[Navigation]}
              style={{
                marginBottom: '15px',
                borderRadius: '10px',
                maxHeight: '70vh',
                width: '100%',
              }}
            >
              {(selectedItem.image_paths || [selectedItem.image_path]).map((src, index) => (
                <SwiperSlide key={index}>
                  <img
                    src={src}
                    alt={`item-${index}`}
                    style={{
                      width: '100%',
                      maxHeight: '70vh',
                      objectFit: 'contain',
                      borderRadius: '10px'
                    }}
                  />
                </SwiperSlide>
              ))}
            </Swiper>

            <h2>{selectedItem.name}</h2>
            <p>{selectedItem.description}</p>
            {selectedItem.rent_price && <p>Rent: ${selectedItem.rent_price}</p>}
            {selectedItem.buy_price && <p>Buy: ${selectedItem.buy_price}</p>}

            <button
                onClick={async () => {
                    const confirmNotify = window.confirm(`Are you sure you want to notify the seller about "${selectedItem.name}"?`);
                    if (!confirmNotify) return;

                    const res = await fetch('/me', { credentials: 'include' });
                    const user = await res.json();

                    if (!user.email || !user.name) {
                    alert('Please log in to contact the seller.');
                    return;
                    }

                    const response = await fetch('/notify_seller', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    credentials: 'include',
                    body: JSON.stringify({
                        buyer_name: user.name,
                        buyer_email: user.email,
                        item_name: selectedItem.name,
                        seller_email: selectedItem.owner_email
                    })
                    });

                    const data = await response.json();
                    if (response.ok) {
                    alert('Seller has been notified!');
                    setSelectedItem(null);
                    } else {
                    alert(data.error || 'Failed to notify seller.');
                    }
                }}
                style={{
                    padding: '12px 24px',
                    borderRadius: '8px',
                    border: '2px solid black',
                    backgroundColor: '#fff',
                    color: '#000',
                    fontWeight: 'bold',
                    fontSize: '1em',
                    cursor: 'pointer',
                    marginTop: '15px',
                    transition: 'all 0.3s ease'
                }}
                onMouseEnter={e => {
                    e.target.style.backgroundColor = '#000';
                    e.target.style.color = '#fff';
                }}
                onMouseLeave={e => {
                    e.target.style.backgroundColor = '#fff';
                    e.target.style.color = '#000';
                }}
                >
                Notify Seller
            </button>

          </div>
        </div>
      )}
    </>
  );
}

const gridStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(4, 1fr)',
  gap: '10px',
  justifyContent: 'center',
  maxWidth: '1200px',
  margin: '0 auto'
};

const cardStyle = {
  borderRadius: '10px',
  padding: '10px',
  background: '#fff',
  textAlign: 'center',
  width: '100%',
  maxWidth: '220px',
  transition: 'transform 0.3s ease, box-shadow 0.3s ease',
  cursor: 'pointer'
};

const imageStyle = {
  width: '100%',
  height: '260px',
  objectFit: 'cover',
  borderRadius: '10px',
  transition: 'transform 0.4s ease, opacity 0.4s ease',
  transform: 'scale(1)',
  boxShadow: 'none'
};

export default Home;
